INSERT INTO `sanpham` VALUES ('50', '4', '1', 'CANON EOS 800D KIT EF S18-55 IS STM', 'Cảm biến: CMOS APS-C 24MP\r\nChip xử lý: DIGIC 7', '<li>Cảm biến: CMOS APS-C 24MP</li>\r\n<li>Chip xử lý: DIGIC 7</li>\r\n<li>Lấy nét AF: 45 điểm cross-type</li>\r\n<li>Công nghệ lấy nét Dual Pixel</li>\r\n<li>Tốc độ chụp liên tục: 6 ảnh/giây</li>\r\n<li>Tốc độ màn trập: 30s-1/4000s</li>\r\n<li>Ống ngắm quan học OVF bao phủ 95%, phóng đại 0.82X</li>\r\n<li>Màn hình LCD cảm ứng xoay lật đa chiều 3 inch</li>\r\n<li>Quay phim: Full HD 60p (tính năng HDR)</li>\r\n<li>Thẻ nhớ: SD/SDHC/SDXC (chuẩn UHS-I)</li>\r\n<li>Pin: LP-E17 (600 tấm)</li>\r\n<li>Kết nối: Wi-fi, NFC, mini HDMI, USB</li></ul>', '21500000', '10', '2', '2017-08-21 19:03:08', null);
INSERT INTO `sanpham` VALUES ('51', '4', '1', 'CANON EOS 70D BODY', 'Cảm biến CMOS 20.2 megapixel, bộ xử lý hình ảnh DIGIC 5+', '<ul><li>- Cảm biến CMOS 20.2 megapixel.</li>\r\n<li>- Bộ xử lý hình ảnh DIGIC 5+</li>\r\n<li>- ISO 100-12800 ( mở rộng 25600)</li>\r\n<li>- Hệ thống đo nét tiên tiến với 19 điểm</li>\r\n<li>- Màn hình LCD 3.0inch cảm ứng</li>\r\n<li>- Tốc độ màn trập 1/8000 giây tới 30 giây</li>\r\n<li>- Tốc độ chụp liên tục tối đa 7.0 hình/giây</li>\r\n<li>- Thẻ nhớ tương thích : SD/ SDHC/SDXC</li>\r\n<li>- Quay phim Full HD</li>\r\n<li>- Kết nối Wifi</li>\r\n<li>- Trọng lượng  675g</li>\r\n<li>- Kích thước bộ lọc ø77mm</li>\r\n<li>- Pin tương thích LP-E6</li></ul>', '23000000', '28', '1', '2017-12-07 19:03:37', null);
INSERT INTO `sanpham` VALUES ('52', '4', '1', 'CANON EOS 5D MARK IV', 'Khung ngắm công nghệ gương phản chiếu 5 mặt (pentaprism), chế độ khử flicker, chụp & quay 4K', '<li>- Cảm biến: 30MP Full-Frame (30.4 triệu điểm ảnh hữu dụng)</li>\r\n<li>- Chip xử lý hình ảnh: DIGIC 6+</li>\r\n<li>- Số điểm nét AF: 61 điểm với điểm lấy nét 41 cross-points</li>\r\n<li>- Dual Pixel RAW</li>\r\n<li>- Loại kính ngắm: Khung ngắm công nghệ gương phản chiếu 5 mặt (pentaprism)</li>\r\n<li>- Màn hình LCD chính: Màn hình tinh thể lỏng màu TFT 3.2 inches, Cảm ứng hoàn toàn,</li>\r\n<li>- Khả năng quay video: 4K</li>\r\n<li>- Chế độ đo sáng: Hệ thống bộ cảm biến đo sáng RGB+IR xấp xỉ 150.000 pixel</li>\r\n<li>- Chế độ khử flicker</li>\r\n<li>- Tích hợp: kết nối Wi-Fi và NFC</li>\r\n<li>- Thẻ nhớ: SD/ SDHC/ SDXC, CompactFlash Type II</li>\r\n<li>- Tuổi thọ pin (theo CIPA): 900 tấm khi sạc đầy</li>\r\n<li>- Cổng kết nối: USB 3.0, HDMI</li>\r\n<li>- Kích thước: 150.7 x 116.4 x 75.9mm</li>\r\n<li>- Khối lượng: 890g</li><ul>', '97290000', '28', '1', '2017-01-28 19:03:56', null);
INSERT INTO `sanpham` VALUES ('53', '4', '2', 'SONY CYBERSHOT DSC-RX1R II', 'Bộ xử lý hình ảnh: BIONZ X, ống ngắm điện tử OLED', '<ul><li>- Độ phân giải: 42.4 Megapixel</li>\r\n<li>- Cảm biến full frame ExmorR CMOS sensor</li>\r\n<li>- ISO 100 – 25600 (mở rộng đến 102400 trong chế độ Multi Frame NR</li>\r\n<li>- Bộ xử lý hình ảnh: BIONZ X</li>\r\n<li>- Ống kính: Carl Zeiss Sonnar T*, 35mm F2</li>\r\n<li>- Bộ lọc chống răng cưa có thể tùy chỉnh được</li>\r\n<li>- Màn hình LCD 3 inch lật (lên 109 độ, xuống 41 độ)</li>\r\n<li>- Ống ngắm điện tử OLED </li>\r\n<li>- Quay phim full HD 1,920x1,080/50p</li>\r\n<li>- Kết nối Wifi/NFC</li>\r\n<li>- Lấy nét tự động hybrid nhanh</li>\r\n<li>- Trọng lượng 507 g\r\n<li>- Pin tương thích NP-BX1</li></ul>', '74900000', '10', '12', null, '10');
INSERT INTO `sanpham` VALUES ('54', '4', '2', 'SONY A7R MARK III', 'Chống rung 5 trục trong thân máy, hệ thống đo sáng thông minh DRO', '<ul><li>- Bộ cảm biến FullFrame Back-illuminated CMOS 42.4 MP</li>\r\n<li>- Chip BIONZ X xử lý nhanh và mạnh hơn 1,8 lần</li>\r\n<li>- Bộ đệm hình ảnh cho phép lưu liên tục 76 ảnh JPEG hoặc 28 ảnh RAW</li>\r\n<li>- Chống rung 5 trục trong thân máy</li>\r\n<li>- Màn hình LCD cảm ứng lật 3.0” có tính năng chạm lấy nét trực tiếp</li>\r\n<li>- Độ nhạy sáng cao ISO 100 - 32.000 (mở rộng lên 102.000)</li>\r\n<li>- Chụp liên tục 10 tấm/giây khi khóa nét bằng EVF và 8 ảnh/giây ở Live View</li>\r\n<li>- Hệ thống lấy nét Fast Hybrid AF nhanh hơn gấp 2 lần</li>\r\n<li>- Tốc độ màn trập 1/8000 - 30s</li>\r\n<li>- Tốc độ ăn đèn 1/250s</li>\r\n<li>- Kính ngắm XGA OLED phủ lớp coat Zeiss T* hiển thị thời gian thực, bao phủ 100% góc nhìn</li>\r\n<li>- Hệ thống đo sáng thông minh DRO</li>\r\n<li>- Quay phim chuẩn 4K không cần thiết bị ngoại vi</li>\r\n<li>- Thêm chế độ Hybrid Log-Gamma cho phép quay phim HDR</li></ul>', '79990000', '16', '2', null, null);
INSERT INTO `sanpham` VALUES ('55', '4', '2', 'SONY A9 BODY', 'Màn trập điện tử: 1/32.000 giây', '<ul style=\"list-style: none;\"><li>- Tốc độ màn trập: 1/8.000 giây, chụp liên tiếp 5 fps</li>\r\n<li>- Màn trập điện tử: 1/32.000 giây</li>\r\n<li>- chụp liên tiếp 20 fps, ISO tối đa 25.600</li>\r\n<li>- Chế độ chụp im lặng: silent mode</li>\r\n<li>- ISO: 100 - 51.200, mở rộng 50 - 204.800, quay phim ISO 100 - 51.200</li>\r\n<li>- Lấy nét: Phase Detection AF 693 điểm</li>\r\n<li>- Kết nối: Wi-Fi n, Bluetooth 4.1, NFC</li>\r\n<li>- Xuất HDMI: 4K 3.840 x 2.160 pixel 30p/25p/24p</li>\r\n<li>- Thẻ nhớ: 2 khe SD, 1 khe hỗ trợ UHS-II</li>\r\n<li>- Kích thước: 126.9 x 95.6 x 63.0 mm</li>\r\n<li>- Trọng lượng: 673 g </li></ul>', '105900000', '36', '3', null, null);
INSERT INTO `sanpham` VALUES ('56', '4', '2', 'SONY RX10 IV', 'Cảm biến Exmor RS™ CMOS 20,1 MP hiệu dụng tích hợp chip DRAM', '<ul><li>Ống kính khẩu độ lớn F2.4-4 ZEISS® Vario-Sonnar T* 24-600 mm</li>\r\n<li>Cảm biến Exmor RS™ CMOS 20,1 MP hiệu dụng tích hợp chip DRAM</li>\r\n<li>Bộ xử lý hình ảnh BIONZ X™ </li>\r\n<li>Tốc độ lấy nét tự động nhanh nhất thế giới  0,03 giây </li>\r\n<li>315 điểm lấy nét tự động </li>\r\n<li>Dò tìm lấy nét tự động, chọn điểm lấy nét dễ dàng với AF qua bảng cảm ứng</li>\r\n<li>Chụp liên tục lên đến 24 hình/giây </li>\r\n<li>Phơi sáng tự động lên đến 2494 lần chụp</li>\r\n<li>Quay phim 4K; Ghi hình dữ liệu Proxy </li>\r\n<li>Cấu hình ảnh, S-Gamut/S-Log2/S-Log3</li>\r\n<li>Hỗ trợ hiển thị hệ số gamma, Zebra, HDMI Clearout, TC/UB, </li>\r\n<li>Điều khiển ghi hình và Thẻ đánh dấu </li></ul>', '40990000', '37', '6', null, null);
INSERT INTO `sanpham` VALUES ('57', '3', '5', 'Panasonic Lumix DMC-FZ2500', 'Độ phân giải 20 MP, quay phim 4K: DCI 4.096 x 2.160 pixel', '<ul style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; color: #222222; font-family: Arial, Helvetica, sans-serif;\">\r\n<li style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background: transparent; text-rendering: geometricPrecision; line-height: 18px;\">Loại m&aacute;y ảnh: ngắm v&agrave; chụp ống k&iacute;nh liền</li>\r\n<li style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background: transparent; text-rendering: geometricPrecision; line-height: 18px;\">Cảm biến: Live MOS 1 độ ph&acirc;n giải 20 MP</li>\r\n<li style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background: transparent; text-rendering: geometricPrecision; line-height: 18px;\">Bộ xử l&yacute; h&igrave;nh ảnh: Venus Engine</li>\r\n<li style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background: transparent; text-rendering: geometricPrecision; line-height: 18px;\">Ống k&iacute;nh: Leica DC Vario-Elmarit 8,8-176mm f/2.8-4.5 ASPH (24-480mm), ổn định h&igrave;nh ảnh 5 trục</li>\r\n<li style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background: transparent; text-rendering: geometricPrecision; line-height: 18px;\">ISO: 80 - 25.600 (video 125 - 6.400)</li>\r\n<li style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background: transparent; text-rendering: geometricPrecision; line-height: 18px;\">Chụp li&ecirc;n tiếp: 12 fps (m&agrave;n trập cơ)</li>\r\n<li style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background: transparent; text-rendering: geometricPrecision; line-height: 18px;\">M&agrave;n h&igrave;nh: 3 độ ph&acirc;n giải 1.04 triệu điểm ảnh, xoay v&agrave; lật đa chiều</li>\r\n<li style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background: transparent; text-rendering: geometricPrecision; line-height: 18px;\">K&iacute;nh ngắm điện tử: OLED độ ph&acirc;n giải 2,36 triệu điểm ảnh, ph&oacute;ng đại 0.74x</li>\r\n<li style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background: transparent; text-rendering: geometricPrecision; line-height: 18px;\">Lấy n&eacute;t: DFD, Post Focus, chạm lấy n&eacute;t</li>\r\n<li style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background: transparent; text-rendering: geometricPrecision; line-height: 18px;\">Quay phim 4K: DCI 4.096 x 2.160 pixel hoặc UHD 3.840 x 2.160 pixel @ 30 fps với 100 Mbps</li>\r\n<li style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background: transparent; text-rendering: geometricPrecision; line-height: 18px;\">Quay phim Full HD: 100 - 200 Mbps, tuỳ chọn 120 fps</li>\r\n<li style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background: transparent; text-rendering: geometricPrecision; line-height: 18px;\">Định dạng ảnh: RAW/JPEG</li>\r\n<li style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background: transparent; text-rendering: geometricPrecision; line-height: 18px;\">Kết nối: W-Fi</li>\r\n<li style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background: transparent; text-rendering: geometricPrecision; line-height: 18px;\">Giao tiếp: USB, HDMI xuất video 4:2:2 10-bit, micro 3,5mm</li>\r\n<li style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background: transparent; text-rendering: geometricPrecision; line-height: 18px;\">Thẻ nhớ: SDHC/SDXC UHS-1 speed class 3</li>\r\n<li style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background: transparent; text-rendering: geometricPrecision; line-height: 18px;\">Pin: 1.200 mAh</li>\r\n<li style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background: transparent; text-rendering: geometricPrecision; line-height: 18px;\">K&iacute;ch thước: 13.76 x 10.19 x 13.47 cm</li>\r\n<li style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background: transparent; text-rendering: geometricPrecision; line-height: 18px;\">Trọng lượng: 966 g</li>\r\n</ul>', '19980000', '30', '37', '2017-12-09 19:09:33', null);
INSERT INTO `sanpham` VALUES ('58', '3', '5', 'Panasonic Lumix DMC-GF5', 'Cable kèm theo:Cable USB,Video Out (NTSC/PAL), AV output,HDMI', '<ul>\r\n<li>Megapixel (Số điểm ảnh hiệu dụng):12.1 Megapixel</li>\r\n<li>Độ lớn m&agrave;n h&igrave;nh LCD(inch):3.0 inch</li>\r\n<li>Độ ph&acirc;n giải ảnh lớn nhất:4000 x 3000</li>\r\n<li>Độ d&agrave;i ti&ecirc;u cự (Focal Length):14-42mm</li>\r\n<li>Độ mở ống k&iacute;nh (Aperture):F3.5-5.6</li>\r\n<li>Tốc độ chụp (Shutter Speed):60 - 1/4000 sec</li>\r\n<li>K&iacute;ch thước cảm biến (Sensor size):Four Thirds (17.3 x 13 mm)</li>\r\n<li>Bộ cảm biến h&igrave;nh ảnh (Image Sensor):CMOS</li>\r\n</ul>', '8300000', '40', '23', '2017-12-11 17:49:48', null);
INSERT INTO `sanpham` VALUES ('59', '2', '5', 'Panasonic DMC-LX100', 'Loại thẻ nhớ:Secure Digital Card (SD), màn hình LCD 3.0 inch', '<ul>\r\n<li>Bộ cảm biến h&igrave;nh ảnh (Image Sensor) Four Thirds (17.3 x 13 mm) CMOS</li>\r\n<li>Megapixel (Số điểm ảnh hiệu dụng):13 Megapixel</li>\r\n<li>Độ nhạy s&aacute;ng (ISO) Auto, 200-25600 (expands to 100-25600)</li>\r\n<li>Độ ph&acirc;n giải ảnh lớn nhất:4112 x 3088</li>\r\n<li>Th&ocirc;ng số về Lens</li>\r\n<li>Độ d&agrave;i ti&ecirc;u cự (Focal Length):24&ndash;75 mm</li>\r\n<li>Độ mở ống k&iacute;nh (Aperture):F1.7 - F2.8</li>\r\n<li>Tốc độ chụp (Shutter Speed):60 - 1/16000 sec</li>\r\n<li>Tự động lấy n&eacute;t (AF):C&oacute;</li>\r\n<li>Optical Zoom (Zoom quang):3.1x</li>\r\n<li>Digital Zoom (Zoom số):4.0x</li>\r\n</ul>', '14500000', '30', '2', '2017-12-14 16:26:30', '0');
